mapper.select:
select
  id, article_id, article_name, article_author, article_introduct, article_time, article_content, article_corpus_id
from zyjblog.t_article


        and id = #{id}


        and article_id = #{articleId}


        and article_name = #{articleName}


        and article_author = #{articleAuthor}


        and article_introduct = #{articleIntroduct}


        and article_time = #{articleTime}


        and article_content = #{articleContent}


        and article_corpus_id = #{articleCorpusId}
