mapper.select:
select
  order_id, buyer_name, buyer_phone, buyer_address, buyer_user_id, order_amount, order_status, pay_status, create_time, update_time
from zyjblog.t_order_master


        and order_id = #{orderId}


        and buyer_name = #{buyerName}


        and buyer_phone = #{buyerPhone}


        and buyer_address = #{buyerAddress}


        and buyer_user_id = #{buyerUserId}


        and order_amount = #{orderAmount}


        and order_status = #{orderStatus}


        and pay_status = #{payStatus}


        and create_time = #{createTime}


        and update_time = #{updateTime}
